import './PanelDropdown.css';
import Checkbox from '../common/Checkbox';
import React from 'react'

class PanelDropdown1 extends React.Component
{

  State = {
    checkedStatuses: {
      "новый": false,
      "расчет": false,
      "подтвержден": false,
      "выполнен": false,
      "отменен": false,
      "отложен": false,
    }
  }

    checkboxChoose = (e) => {
      var{name, checked} = e.target;

      this.setState((e) => {
        var selectedStatus = e.State.checkedStatuses;
        return selectedStatus[name]=checked;
      });
    };

  render()
  {


    var selectedStatuses = Object.keys(this.State.checkedStatuses).filter((x) => this.State.checkedStatuses[x]);

    return(
      <div className="dropdown">
        <ul className="dropdown__list">
        <input type="checkbox" className="checkbox__item" name="новый" value="новый" onChange={this.checkboxChoose}/>
        <input type="checkbox" className="checkbox__item" name="расчет" value="расчет" onChange={this.checkboxChoose}/>
        </ul>
        {selectedStatuses}
      </div>
    );
  }
}

export default PanelDropdown1;
