import './PanelDropdown.css';
import Checkbox from '../common/Checkbox';
import React from 'react'

class PanelDropdown1 extends React.Component
{

  State = {
    checkedStatuses: {
      новый: false,
      расчет: false,
      подтвержден: false,
      выполнен: false,
      отменен: false,
      отложен: false,
    }
  }

    checkboxChoose = (e) => {
      var {name, checked} = e.target;

      this.setState((e) => {
        var selectedStatus = e.State.checkedStatuses;
        return selectedStatus[name]=checked;
      });
    };

  render()
  {

    var selectedStatuses = Object.keys(this.State.checkedStatuses).filter((x) => this.State.checkedStatuses[x]);

    const Status = [
      "новый",
      "расчет",
      "подтвержден",
      "выполнен",
      "отменен",
      "отложен",
    ];

    const DropdownData = Status.map((label) => {
      return (
        <li className="dropdown__option">
            <Checkbox
              label={label}
              name={label}
              onChange={this.checkboxChoose}
            ></Checkbox>
        </li>
      );
    });

    return(
      <div className="dropdown">
        <ul className="dropdown__list">{DropdownData}</ul>
        {selectedStatuses}
      </div>
    );
  }
}

export default PanelDropdown1;
