import './RightSideTableBody.css';
import React, { useState } from 'react';
import OrderDetails from '../../data/OrderDetails.json'
import { connect } from 'react-redux';
import { selectors } from "../../data";

function RightSideTableBody({ orderId }) {

  let [data] = useState(OrderDetails)
  data = data.filter((d) => (d.id === orderId))

  return (
        <table>
            <thead>
                <th>Артикул</th>
                <th>Наименование</th>
                <th>Цена</th>
            </thead>
            <tbody>
                {data.map((d)=>(
                    <tr key={d.id}>
                        <td>{d.articul}</td>
                        <td>{d.name}</td>
                        <td>{d.price}</td>
                    </tr>
                ))
                }
            </tbody>
        </table>
  );
}

const mapStateToProps = function(state) {

    return {
        orderId: selectors.getOrderId(state)
    }

  }

  export default connect(mapStateToProps)(RightSideTableBody);
