import './PanelDropdown.css';
import Checkbox from '../common/Checkbox';

const Status = [
  "новый",
  "расчет",
  "подтвержден",
  "выполнен",
  "отменен",
  "отложен",
];

const DropdownData = Status.map((label) => {
  const State = {
    "новый": false,
    "расчет": false,
    "подтвержден": false,
    "выполнен": false,
    "отменен": false,
    "отложен": false,
  }
  const checkboxChoose = (e) => {
    var {name, checked} = e.target;

    this.setState((e) => {
      var selectedStatus = e.State;
      return selectedStatus[name] = checked;
    });
  }
  const checkedStatuses = Object.keys(this.state.State)
  return (
    <li className="dropdown__option">
        <Checkbox
          label={label}
          name={label}
          onChange="this.checkboxChoose"
        ></Checkbox>
    </li>
  );
});

function PanelDropdown() {
  return (
    <div className="dropdown">
      <ul className="dropdown__list">{DropdownData}</ul>
    </div>
  );
}

export default PanelDropdown;
