import './PanelDropdown.css';
import Checkbox from '../common/Checkbox';
import React from 'react';

class PanelDropdown extends React.Component
{
  render()
  {
Status = [
  "новый",
  "расчет",
  "подтвержден",
  "выполнен",
  "отменен",
  "отложен",
];

State = {
  "новый": false,
  "расчет": false,
  "подтвержден": false,
  "выполнен": false,
  "отменен": false,
  "отложен": false,
}
  checkboxChoose = (e) => {
  var {name, checked} = e.target;

  this.setState((e) => {
    var selectedStatus = e.State;
    return selectedStatus[name] = checked;
  });
}

checkedStatuses = Object.keys(this.state.State)

DropdownData = Status.map((label) => {
  return (
    <li className="dropdown__option">
        <Checkbox
          label={label}
          name={label}
          onChange="this.checkboxChoose"
        ></Checkbox>  
    </li>
  );
});

{
  return (
    <div className="dropdown">
      <ul className="dropdown__list">{DropdownData}</ul>
      {checkedStatuses} 
    </div>
  );
}
}
}
export default PanelDropdown;
