import './Table.css';
import Checkbox from '../common/Checkbox';
import React, { useState } from 'react';
import Orders from '../../data/Orders.json'

const StatusClass = {
    Новый: "status-new",
    Расчет: "status-inprogress",
    Выполнен: "status-done",
    Отменен: "status-canceled",
    Отложен: "status-delayed",
  };

function Table() {
    const [data, setdata] = useState(Orders)
    const [order, setorder] = useState("ASC")
    const sorting = (col) => {
        if (order ==="ASC") {
            const sorted = [...data].sort((a,b) =>
                a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
            );
            setdata(sorted);
            setorder("DSC");
        }
        if (order ==="DSC") {
            const sorted = [...data].sort((a,b) =>
                a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
            );
            setdata(sorted);
            setorder("ASC");
        }
    }
  return (
    <div className="table">
        <table>
            <thead>
                <th><Checkbox/></th>
                <th>#</th>
                <th onClick={()=>sorting("created")}>Дата</th>
                <th onClick={()=>sorting("status")}>Статус</th>
                <th onClick={()=>sorting("positions")}>Позиций</th>
                <th onClick={()=>sorting("sum")}>Сумма</th>
                <th>ФИО покупателя</th>
            </thead>
            <tbody>
                {data.map((d)=>(
                    <tr key={d.id}>
                        <td><Checkbox/></td>
                        <td>{d.id}</td>
                        <td>{d.created}</td>
                        <td className={StatusClass[d.status]}>{d.status}</td>
                        <td>{d.positions}</td>
                        <td>{d.sum}</td>
                        <td>{d.name}</td>
                    </tr>
                ))
                }
            </tbody>
        </table>
    </div>
  );
}

export default Table;
