import './Input.css';

import { ReactComponent as IconAbort    } from './icons/abort.svg';
import { ReactComponent as IconBin      } from './icons/bin.svg';
import { ReactComponent as IconCheckmark} from './icons/checkmark.svg';
import { ReactComponent as IconDot      } from './icons/dot.svg';
import { ReactComponent as IconFilter   } from './icons/filter.svg';
import { ReactComponent as IconLocked   } from './icons/locked.svg';
import { ReactComponent as IconMoon     } from './icons/moon.svg';
import { ReactComponent as IconPencil   } from './icons/pencil.svg';
import { ReactComponent as IconRefresh  } from './icons/refresh.svg';
import { ReactComponent as IconSearch   } from './icons/search.svg';
import { ReactComponent as IconSun      } from './icons/sun.svg';
import { ReactComponent as IconVarrow   } from './icons/v_arrow.svg';
import { ReactComponent as IconXlarge   } from './icons/x-large.svg';
import { ReactComponent as IconXmedium  } from './icons/x-medium.svg';

const Icons = [
  <IconAbort     className="icon"/>,
  <IconBin       className="icon"/>,
  <IconCheckmark className="icon"/>,
  <IconDot       className="icon"/>,
  <IconFilter    className="icon"/>,
  <IconLocked    className="icon"/>,
  <IconMoon      className="icon"/>,
  <IconPencil    className="icon"/>,
  <IconRefresh   className="icon"/>,
  <IconSearch    className="icon"/>,
  <IconSun       className="icon"/>,
  <IconVarrow    className="icon"/>,
  <IconXlarge    className="icon"/>,
  <IconXmedium   className="icon"/>,
];

const Input = ({
  label,
  className,
  iconLeft,
  iconRight,
  prefixClassName="hidden",
  prefixLabel,
  placeholder,
  value=null,
}) => {
  return (
    <div className="input">
        <label className="input__label" for="input">{label}</label>
        <div className={`input__input_group ${className}`}>
          <span className="input_group__icon-left">
            {Icons[iconLeft]!==null ? Icons[iconLeft] : <></>} 
          </span>
          <span className={`input_group__prefix ${prefixClassName}`}>{prefixLabel}</span>
          <input type="text" className="input_group__text" placeholder={placeholder} value={value}/>
          <span className="input_group__icon-right">
          {Icons[iconRight]!==null ? Icons[iconRight] : <></>} 
          </span>
        </div>
    </div>
  );
}

export default Input;
