import './TableFooter.css';
import Button from '../common/Button';
import TableFooterDropdown from './TableFooterDropdown';
import React from 'react';
import { actions, selectors, store } from '../../data'
import { pagingReducer } from '../../data/reducers/paging_reducer';
import { useSelector } from 'react-redux';

function TableFooter() {

    const Pages = [1, 2, 3, 4, 5]
    const currentPage = useSelector(state => state.selectors.getCurrentPage)

    return (
        <div className="table__footer">
            <div className="table__footer-left">
                <div className="table__footer-selected">
                    Выбрано записей: 
                    <span className="table__footer-selected"> 4</span>
                </div>
                <Button 
                        className="button-tiny_solid"
                        text="Изменить статус"
                        icon={7}
                ></Button>
                <Button 
                        className="button-tiny_solid button-tiny_solid-danger"
                        text="Удалить"
                        icon={1}
                ></Button>
                <TableFooterDropdown/>
            </div>
            <div className="table__footer-pagination">
                {Pages.map((page, index) => 
                <Button 
                        key={index} 
                        className={currentPage == page ? "button-tiny_solid button-tiny_solid-text_only" : "button-tiny_transparent button-tiny_transparent-text_only"}
                        text={index} 
                        onClick={() => {
                                store.dispatch(actions.ordersPagingAction(page-1));
                            }}
                ></Button>)}
                <div className="table__footer-pagination-last">
                    <Button 
                            className="button-tiny_transparent button-tiny_transparent-text_only"
                            text="#"
                    ></Button>
                </div>
            </div>
        </div>
    );
}

export default TableFooter;
